import React from 'react';

function Navbar() {
  return (
    <nav className="navbar">
      <div className="nav-container">
        <a href="#hero" className="nav-logo">Monesh Kovi</a>
        <ul className="nav-menu">
          <li><a href="#about">About</a></li>
          <li><a href="#projects">Projects</a></li>
          <li><a href="#experience">Experience</a></li>
          <li><a href="#certifications">Certifications</a></li>
          <li>
            <a
              href="https://www.linkedin.com/in/monesh-kovi/"
              target="_blank"
              rel="noopener noreferrer"
            >
              LinkedIn
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
}

function Hero() {
  return (
    <section id="hero" className="hero">
      <div className="hero-content">
        {/* Ensure you add your profile image (with background removed) to the public folder as "profile.jpg" */}
        <img src="/profile.jpg" alt="Monesh Kovi" className="profile-image" />
        <h1>Monesh Kovi</h1>
        <p>Cloud Architect & AI Enthusiast</p>
        <div className="hero-cta">
          <a
            href="https://github.com/moneshkovi"
            target="_blank"
            rel="noopener noreferrer"
            className="btn"
          >
            GitHub
          </a>
          <a
            href="https://www.linkedin.com/in/monesh-kovi/"
            target="_blank"
            rel="noopener noreferrer"
            className="btn"
          >
            LinkedIn
          </a>
        </div>
      </div>
    </section>
  );
}

function About() {
  return (
    <section id="about" className="section about">
      <h2>About Me</h2>
      <p>
        I am a passionate Cloud Architect and AI Enthusiast with a strong background in designing secure, scalable cloud solutions and innovative AI applications. Currently pursuing my Master's in Information Systems, I thrive on solving complex problems and building systems that drive innovation.
      </p>
    </section>
  );
}

function Projects() {
  return (
    <section id="projects" className="section projects">
      <h2>Projects</h2>
      <div className="project-cards">
        <div className="project-card">
          <h3>AI-Powered Content Platform</h3>
          <p>
            A platform that integrates AWS services and GPT-4 API for automated content generation.
          </p>
          <a href="#" className="btn">View Project</a>
        </div>
        <div className="project-card">
          <h3>Emotion Recognition (EmoBERTa)</h3>
          <p>
            An advanced NLP model using the Roberta architecture to detect and analyze emotions.
          </p>
          <a href="#" className="btn">View Project</a>
        </div>
      </div>
    </section>
  );
}

function Experience() {
  return (
    <section id="experience" className="section experience">
      <h2>Experience</h2>
      <div className="experience-item">
        <h3>Cloud Architect Intern - Alphathena</h3>
        <p>
          Optimized website hosting with AWS Elastic Beanstalk and AWS Amplify.
        </p>
      </div>
      <div className="experience-item">
        <h3>Software Engineer Intern - Wrecked Esports</h3>
        <p>
          Designed and deployed an internal reporting dashboard to enhance decision-making.
        </p>
      </div>
      {/* Additional experience items can be added here */}
    </section>
  );
}

function Certifications() {
  return (
    <section id="certifications" className="section certifications">
      <h2>Certifications</h2>
      <ul>
        <li>AWS Certified Developer – Amazon Web Services</li>
        <li>AWS Certified Solutions Architect – Amazon Web Services</li>
      </ul>
    </section>
  );
}

function Contact() {
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      name: e.target.name.value,
      email: e.target.email.value,
      message: e.target.message.value
    };

    // Send data to the Flask backend
    fetch('http://localhost:5000/api/contact', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        e.target.reset();
      })
      .catch(error => {
        console.error('Error:', error);
        alert('Something went wrong. Please try again later.');
      });
  };

  return (
    <section id="contact" className="section contact">
      <h2>Contact Me</h2>
      <form onSubmit={handleSubmit} className="contact-form">
        <input type="text" name="name" placeholder="Your Name" required />
        <input type="email" name="email" placeholder="Your Email" required />
        <textarea name="message" placeholder="Your Message" required></textarea>
        <button type="submit" className="btn">Send</button>
      </form>
    </section>
  );
}

function App() {
  return (
    <div className="App">
      <Navbar />
      <Hero />
      <About />
      <Projects />
      <Experience />
      <Certifications />
      <Contact />
      <footer className="footer">
        <p>&copy; {new Date().getFullYear()} Monesh Kovi. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
